.draw( self.source, self.x, self.y, 0, 1, 1, self.w / 2, self.h / 2 )  love.graphics.setBlendMode('premultiplied')  love.graphics.draw( self.name_canvas )  love.graphics.setBlendMode('alpha')  self.hp_bar:draw()  self.mp_bar:draw()    love.graphics.setColor( original_color )end----------------------------------------------------------------------- * Frame update.---------------------------------------------------------------------function Ally:refresh()  love.graphics.setCanvas( self.name_canvas )  self.name_canvas:clear()  love.graphics.print_outline( self.name, self.x, self.y - self.h / 2, 0, 1, 1, self.font:getWidth( self.name ) / 2 )  love.graphics.setCanvas()end----------------------------------------------------------------------- * Frame update.---------------------------------------------------------------------function Ally:mousepressed( x, y, key )  if within_area( x, y, self.x, self.y, self.w, self.h ) and self.movable then    self.locked_on = not self.locked_on  endend----------------------------------------------------------------------- * Frame update.---------------------------------------------------------------------function Ally:coordinates()  return self.x, self.yend