---- Graphics------------------------------------------------------------------------- * Print outlined text on screen.-- param ::= text (string) - Text string to draw.-- param ::= x (numeric or string) - X position.-- param ::= y (numeric or string) - Y position.-- param ::= r (numeric) - Orientation in radians.-- param ::= sx (numeric) - Scale factor (x-axis)-- param ::= sy (numeric) - Scale factor (y-axis)-- param ::= ox (numeric) - Origin offset (x-axis)-- param ::= oy (numeric) - Origin offset (y-axis)-- param ::= kx (numeric) - Shearing factor (x-axis)-- param ::= ky (numeric) - Shearing factor (y-axis)---------------------------------------------------------------------function love.graphics.print_outline( text, x, y, r, sx, sy, ox, oy, kx, ky, c1, c2 )  local original_color = { love.graphics.getColor() }    if type( x ) == 'string' then    if x:lower() == 'left' then      x = 5    elseif x:lower() == 'center' then      x = WINDOW_WIDTH / 2 - FONT:getWidth( text ) / 2    elseif x:lower() == 'right' then      x = WINDOW_WIDTH - FONT:getWidth( text ) - 5    end  end  if type( y ) == 'string' then    if y:lower() == 'top' then      y = 5    elseif y:lower() == 'center' then      y = WINDOW_HEIGHT / 2 - FONT:getHeight() * 3 / 2    elseif y:lower() == 'bottom' then      y = WINDOW_HEIGHT - FONT:getHeight() * 3 - 4    end  end  if c1 == nil then c1 = Color.white end  if c2 == nil then c2 = Color.black end    love.graphics.setColor( c2 )  love.graphics.print( text,     x, y - 1, r, sx, sy, ox, kx, ky ) -- up  love.graphics.print( text, x + 1,     y, r, sx, sy, ox, kx, ky ) -- right  love.graphics.print( text,     x, y + 1, r, sx, sy, ox, kx, ky ) -- down  love.graphics.print( text, x - 1,     y, r, sx, sy, ox, kx, ky ) -- left  love.graphics.print( text, x - 1, y - 1, r, sx, sy, ox, kx, ky ) -- upper left  love.graphics.print( text, x + 1, y - 1, r, sx, sy, ox, kx, ky ) -- upper right  love.graphics.print( text, x - 1, y + 1, r, sx, sy, ox, kx, ky ) -- lower left  love.graphics.print( text, x + 1, y + 1, r, sx, sy, ox, kx, ky ) -- lower right  love.graphics.setColor( c1 )  love.graphics.print( text, x, y, r, sx, sy, ox, kx, ky ) -- center    love.graphics.setColor( original_color )end