---- Graphics------------------------------------------------------------------------- * Print outlined text on screen.-- param ::= text (string) - Text string to draw.-- param ::= x (numeric) - X position.-- param ::= y (numeric) - Y position.-- param ::= r (numeric) - Orientation in radians.-- param ::= sx (numeric) - Scale factor (x-axis)-- param ::= sy (numeric) - Scale factor (y-axis)-- param ::= ox (numeric) - Origin offset (x-axis)-- param ::= oy (numeric) - Origin offset (y-axis)-- param ::= kx (numeric) - Shearing factor (x-axis)-- param ::= ky (numeric) - Shearing factor (y-axis)---------------------------------------------------------------------function love.graphics.print_outline( text, x, y, r, sx, sy, ox, oy, kx, ky, c1, c2 )  local original_color = { love.graphics.getColor() }    if c1 == nil then c1 = Color.white end  if c2 == nil then c2 = Color.black end    love.graphics.setColor( c2 )  love.graphics.print( text,     x, y - 1, r, sx, sy, ox, kx, ky ) -- up  love.graphics.print( text, x + 1,     y, r, sx, sy, ox, kx, ky ) -- right  love.graphics.print( text,     x, y + 1, r, sx, sy, ox, kx, ky ) -- down  love.graphics.print( text, x - 1,     y, r, sx, sy, ox, kx, ky ) -- left  love.graphics.print( text, x - 1, y - 1, r, sx, sy, ox, kx, ky ) -- upper left  love.graphics.print( text, x + 1, y - 1, r, sx, sy, ox, kx, ky ) -- upper right  love.graphics.print( text, x - 1, y + 1, r, sx, sy, ox, kx, ky ) -- lower left  love.graphics.print( text, x + 1, y + 1, r, sx, sy, ox, kx, ky ) -- lower right  love.graphics.setColor( c1 )  love.graphics.print( text, x, y, r, sx, sy, ox, kx, ky ) -- center    love.graphics.setColor( original_color )end